import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send the signin data to your backend API
    try {
      const response = await axios.post('/signin', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        console.log('Successfully signed in');
        window.alert('Successfully signed in');
        navigate('/');
        // Redirect the user to the dashboard or another protected route
      } else if (response.status === 400) {
        console.error('Incorrect password');
        window.alert('Incorrect email address');
        // Display an error message to the user
      } else if (response.status === 401) {
        console.error('User not found');
        window.alert('User not found');
        // Display an error message to the user
      } else {
        console.error('Signin failed');
        window.alert('Signin failed');
        // Display an error message to the user
      }
    } catch (error) {
      console.error('Error during signin:', error);
    }
  };

  return (
    <div>
      <h2>Signin</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Signin</button>
        <Link className="nav-link" to="/forgot-pass">
          Forgot Password
        </Link>
      </form>
    </div>
  );
};

export default Login;
